import { useEffect, useState } from "react"
import { getArticleText } from "../../api/api"
import { IArticle } from "../../models/IArticle"
import "./ReadingView.css"

interface IProps {
    article?: IArticle
    onClose: () => void
}

export const ReadingView = (props: IProps) => {
    const [article, setArticle] = useState<IArticle | undefined>(props.article)
    const [text, setText] = useState<string>()

    useEffect(() => {
        setArticle(props.article)
    }, [props.article])

    useEffect(() => {
        if (!article) {
            setText(undefined)
            return
        }

        const fetchData = async () => {
            const fetchedText = await getArticleText(article!)
            setText(
                fetchedText
                    ?.replaceAll(/^\s+|\s+$/gm, " ")
                    ?.replaceAll(/<\/?[a-z][a-z0-9]*[^<>]*>/gi, "")
                    ?.replaceAll("&nbsp; ", "")
            )
        }

        fetchData()
    }, [article])

    return (
        <div
            className="expanded-panel"
            style={{
                transform: `translate(0, ${article ? "0" : "100vh"})`,
            }}
        >
            <button className="close" onClick={() => setArticle(undefined)}>
                Close
            </button>

            {!text ? (
                <span className="loading">Loading...</span>
            ) : (
                <span className="text">{text}</span>
            )}
        </div>
    )
}
