import { useEffect, useState } from "react"
import { getArticleText } from "../../api/api"
import { IArticle } from "../../models/IArticle"
import "./ReadingView.css"

interface IProps {
    article?: IArticle
    onClose: () => void
}

export const ReadingView = (props: IProps) => {
    const [isExpanded, setIsExpanded] = useState<boolean>(false)
    const [text, setText] = useState<string>()

    useEffect(() => {
        if (!props.article) {
            setIsExpanded(false)
            return
        }

        setIsExpanded(true)

        const fetchData = async () => {
            const fetchedText = await getArticleText(props.article!)
            setText(
                fetchedText
                    ?.replaceAll(/^\s+|\s+$/gm, " ")
                    ?.replaceAll(/<\/?[a-z][a-z0-9]*[^<>]*>/gi, "")
                    ?.replaceAll("&nbsp;", "")
                    ?.trim()
            )
        }

        fetchData()
    }, [props.article])

    useEffect(() => {
        if (!isExpanded) {
            // Wait for the close animation
            const oldText = text
            setTimeout(() => {
                setText((newText) => {
                    if (oldText === newText) {
                        return undefined
                    }

                    return newText
                })

            }, 500)
        }
    }, [isExpanded, text])

    return (
        <div
            className="expanded-panel"
            style={{
                transform: `translate(0, ${isExpanded ? "0" : "100vh"})`,
            }}
        >
            <button className="close" onClick={() => setIsExpanded(false)}>
                Close
            </button>

            {!text ? (
                <span className="loading">Loading...</span>
            ) : (
                <>
                    <span className="text">{text}</span>
                    <a href={props.article?.link} className="link">
                        â†— View on ANSA.it
                    </a>
                </>
            )}
        </div>
    )
}
